import React from "react";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardActions from "@mui/material/CardActions";
import { Button, Avatar } from "@mui/material";
import { Link } from "react-router-dom";
import { useState } from "react";

const CardOdontologo = ({ name, username, id }) => {

  const [addFavorite, setaddFavorite] = useState([])

  const addFav = (added) => {
    // Aqui iria la logica para agregar la Card en el localStorage

    console.log("hla desde boton")
    
    const favorites = localStorage.getItem('favorites');
    if(favorites){
      let parseFavorites = JSON.parse(favorites);
      const filteredDentist = parseFavorites.filter(dentist => dentist !== added)
      const exists = filteredDentist.length !== parseFavorites.length;

      exists ? parseFavorites = filteredDentist : parseFavorites.push(added)
      localStorage.setItem('favorites', JSON.s)
    }

  };

  return (
    <div className="card">
      {/* En cada card deberan mostrar en name - username y el id */}

      {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

      {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
      
        <Card sx={{ maxWidth: 345 }}>
        <Link to={`/dentist/${id}`} key={id}>
          <CardMedia
            component="img"
            height="194"
            image="/images/doctor.jpg"
            alt="img doctor"
          />
          <CardHeader
            title={name}
            subheader={username}
            avatar={<Avatar sx={{ bgcolor: "#8561c5" }}>{id}</Avatar>}
          />
          </Link>
          <CardActions
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button
              onClick={addFav}
              type="button"
              variant="contained"
              color="primary"
            >
              Add fav
            </Button>
          </CardActions>
        </Card>
      
    </div>
  );
};

export default CardOdontologo;
