import React from "react";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardActions from "@mui/material/CardActions";
import { Button, Avatar } from "@mui/material";
import { Link } from "react-router-dom";
import { useState } from "react";

const CardOdontologo = ({ name, username, id }) => {

  const [data, setdata] = useState([])

  const addFav = () => {
    // Aqui iria la logica para agregar la Card en el localStorage

  console.log("hla desde boton")
    let odontologoData = {
      id: id,
      name: name,
      username: username,
    };

    if (localStorage.getItem("favs") == null) {
      localStorage.setItem("favs", []);
    }

    let data = JSON.parse(localStorage.getItem("favs"));
    data.push(odontologoData);

    localStorage.setItem("favs", JSON.stringify(data));
  };

  return (
    <div className="card">
      {/* En cada card deberan mostrar en name - username y el id */}

      {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

      {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
      
        <Card sx={{ maxWidth: 345 }}>
        <Link to={`/dentist/${id}`} key={id}>
          <CardMedia
            component="img"
            height="194"
            image="/images/doctor.jpg"
            alt="img doctor"
          />
          <CardHeader
            title={name}
            subheader={username}
            avatar={<Avatar sx={{ bgcolor: "#8561c5" }}>{id}</Avatar>}
          />
          </Link>
          <CardActions
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button
              onClick={()=> addFav(id)}
              type="button"
              variant="contained"
              color="primary"
            >
              Add fav
            </Button>
          </CardActions>
        </Card>
      
    </div>
  );
};

export default CardOdontologo;
