import { useReducer, useState } from "react";
import { createContext } from "react";


export const ContextGlobal = createContext();

const reducerFunction = (state, { type }) => {
  switch (type) {
    case "dark":
      return { bgFlag: "LIGHT",
      bgColor: "#393944",
      ftColor: "#eee"};
    case "light":
      return { bgFlag: "DARK",
      ftColor: "#393944",
      bgColor: "#eee" };
    default:
      return state;
  }
};

